     @SuppressWarnings("resource")
     private int getIndexAlFabeto(String moneda) {
         Scanner scAlFa = new Scanner(alFabeto).useDelimiter("-");
         for (int indexAlFa = 0; scAlFa.hasNext(); indexAlFa++) {
             if (moneda.equals(scAlFa.next()))
                 return indexAlFa;
         }
         return -1;
    }

